import styled from "styled-components";


export const Controls = (children) => {
    const Wrapper = styled.div`
    width: 500px;
    margin: auto;
    background-color: red;
    padding: 5px;
    `;
    const Button = styled.button`
        width: 2em;
        height: 2em;
    `
    return (
        <Wrapper>
          <Button>▶️</Button>  
          <button style={{display: 'none'}}>⏸️</button>  
          <button>⏭️</button>  
        </Wrapper>
    )
}
